import { statSync } from 'fs';
import path from 'path';

function localResolver() {
  return {
    resolveId: function (importee, importer) {
      if (importee.indexOf('./') === -1) {
        return null;
      }

      if (!importer) {
        return null;
      }

      var basename = path.basename(importer);
      var directory = importer.split(basename)[0];

      var dirIndexFile = path.join(directory + importee, 'index.js');

      // TODO: This should be asynchronous
      var stats = undefined;

      try {
        stats = statSync(dirIndexFile);
      } catch (e) {
        return null;
      }

      if (stats.isFile()) {
        return dirIndexFile;
      }

      return null;
    }
  };
}

export default localResolver;